"use client"

import { motion, useInView } from "framer-motion"
import { useRef } from "react"
import Image from "next/image"
import Link from "next/link"

const experiences = [
  {
    company: "Job Gap",
    url: "https://uk.linkedin.com/company/jobgapai",
    img: "data:image/jpeg;base64,",
    role: "Frontend Developer (Contract)",
    period: "October 2025 – December 2025",
    description:
      "Worked on an AI-powered job application platform focused on resume analysis and job matching. Optimized AI usage costs by caching resume feedback and job match results in local storage, replacing automatic regeneration with user-controlled refresh actions. Built employer and candidate dashboards displaying resume scores, matched jobs, and application insights. Implemented an AI-assisted email generator that compares job descriptions against stored resumes to shortlist candidates and streamline hiring decisions. Fixed state synchronization issues and improved responsiveness across key user flows.",
  },
  {
    company: "Blucon Valley",
    url: "https://ng.linkedin.com/in/blucon-valley-2641321b3",
    img: "data:image/jpeg;base64,",
    role: "Frontend Developer (Contract)",
    period: "August 2025 – October 2025",
    description:
      "Built core frontend features for a role-based marketplace platform connecting buyers and sellers. Implemented multi-step onboarding flows with persistent form state to prevent data loss on refresh or navigation. Developed product listing and detail pages with advanced filtering and responsive layouts. Built seller dashboards for managing ads, customer messages, and top buyers, alongside buyer profile dashboards. Ensured smooth role-based redirection and consistent mobile responsiveness across the platform.",
  },
  {
    company: "Ojutu Inc",
    url: "https://www.linkedin.com/company/ojutu-co/",
    img: "data:image/png;base64,",
    role: "Frontend Developer",
    period: "August 2024 – July 2025",
    description:
      "Worked at a software development agency contributing to multiple production-grade applications, including EdTech, HealthTech, and SaaS platforms. Built and optimized frontend features using React and Next.js, implementing full user flows from onboarding to dashboards. Collaborated closely with backend engineers and designers to integrate APIs, handle state management, and ensure scalable, responsive UI implementations across different products.",
  },
  {
    company: "Efortlex Limited",
    url: "https://www.linkedin.com/company/efortlexafrica.ng/posts/",
    img: "data:image/jpeg;base64,",
    role: "Frontend Developer Intern",
    period: "April 2024 – August 2024",
    description:
      "Built and maintained a property listing platform connecting tenants—mostly students—with landlords based on location proximity. Developed property listing and detail pages, implemented API-driven data rendering, and managed component state for search and filtering functionality. Translated Figma designs into production-ready, responsive user interfaces while working with real backend endpoints.",
  },
  {
    company: "Loyalty Solutions",
    url: "https://www.linkedin.com/company/loyalty-solutions-nigeria/posts/?feedView=all",
    img: "data:image/jpeg;base64,",
    role: "Frontend Developer (Contract)",
    period: "February 2024 – April 2024",
    description:
      "Developed UI components and dashboards for a customer loyalty and rewards platform. Implemented dynamic reward logic and integrated multiple API endpoints to display user points, rewards, and redemption history. Worked on responsive layouts and improved dashboard usability to support both admin and customer-facing views.",
  },
]

export default function Experience() {
  const ref = useRef(null)
  const isInView = useInView(ref, { once: true, margin: "-100px" })

  return (
    <section className="py-20 bg-white">
      <div className="max-w-4xl mx-auto px-6">
        <motion.h2
          ref={ref}
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6 }}
          className="text-3xl md:text-4xl font-bold text-black mb-12 text-center"
        >
          Experience
        </motion.h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {experiences.map((exp, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
              transition={{ duration: 0.5, delay: index * 0.08 }}
              className="bg-white rounded-lg shadow-sm hover:shadow-md border border-gray-100 p-6 flex flex-col"
            >
              <div className="flex items-center gap-4">
                <Link href={exp.url} target="_blank" className="flex-shrink-0">
                  <Image src={exp.img} alt={exp.company} width={60} height={60} className="rounded-full object-cover" />
                </Link>
                <div>
                  <Link href={exp.url} target="_blank">
                    <h3 className="text-lg font-semibold text-gray-900 hover:text-blue-600">{exp.company}</h3>
                  </Link>
                  <p className="text-sm text-gray-600">{exp.role}</p>
                  <p className="text-xs text-gray-400">{exp.period}</p>
                </div>
              </div>

              <p className="mt-4 text-gray-700 text-sm flex-1">{exp.description}</p>

              <div className="mt-4">
                <Link href={exp.url} target="_blank" className="text-sm text-blue-600 hover:underline">
                  View
                </Link>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  )
}
